#include <unordered_set>
#include <vector>

using namespace std;

class Solution {
public:
    int findCircleNum(vector<vector<int>>& isConnected) {
        vector<int> front;
        //front.push_back(0);
        int res = 0;
        unordered_set<int> visited_towns;
        while (visited_towns.size() != isConnected.size()) 
        {
            // ищем первый непосещенный город
            for (int i = 0; i < isConnected.size();i++)            
            { 
                // если не посещали этот гррордл
                if (visited_towns.find(i) ==visited_towns.end())               
                {
                    front.push_back(i);
                    break;
                }
            }

            while (!front.empty()) 
            {
                vector<int> next_front;
                for (int i = 0; i < front.size();i++)
                {
                    visited_towns.insert(front[i]); // ! посчетили город
                    for (int j = 0; j < isConnected[front[i]].size();j++)            
                    { 
                        // если города связаны и еще не посещен j город
                        if(isConnected[front[i]][j]==1 && front[i]!=j && (visited_towns.find(j) ==visited_towns.end()))
                        {
                            next_front.push_back(j);
                        }
                    }
                }
                front = next_front;
            }
            res++; // прошли все связанные меж собой города в одгой провинции
        }
        return res;
    }
};

