class Solution {
public:

    int findCircleNum(vector<vector<int>>& isConnected) {
        unordered_set<int> front;
        //front.push_back(0);
        int res = 0;
        unordered_set<int> visited_towns;
        while (visited_towns.size() != isConnected.size()) 
        {
            // ищем первый непосещенный город
            for (int i = 0; i < isConnected.size();i++)            
            { 
                // если не посещали этот гррордл
                if (visited_towns.find(i) ==visited_towns.end())               
                {
                    front.insert(i);
                    break;
                }
            }

            while (!front.empty()) 
            {
                unordered_set<int> next_front;
                for (auto front_i : front)
                {
                    visited_towns.insert(front_i); // ! посчетили город
                    for (int j = 0; j < isConnected[front_i].size();j++)            
                    { 
                        // если города связаны и еще не посещен j город
                        if(isConnected[front_i][j]==1 && front_i!=j && (visited_towns.find(j) ==visited_towns.end()))
                        {
                            next_front.insert(j);
                        }
                    }
                }
                front = next_front;
            }
            res++; // прошли все связанные меж собой города в одгой провинции
        }
        return res;
    }
};